contracts/MicroloanSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title MicroloanSystem
 * @dev Smart contract for managing microloans with credit scoring and automated repayments
 */
contract MicroloanSystem is ReentrancyGuard, Pausable, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Events
    event LoanRequested(uint256 indexed loanId, address indexed borrower, uint256 amount, uint256 duration);
    event LoanApproved(uint256 indexed loanId, address indexed lender, uint256 amount, uint256 interestRate);
    event LoanFunded(uint256 indexed loanId, uint256 amount);
    event RepaymentMade(uint256 indexed loanId, address indexed borrower, uint256 amount, uint256 installment);
    event LoanCompleted(uint256 indexed loanId, address indexed borrower);
    event LoanDefaulted(uint256 indexed loanId, address indexed borrower);
    event LenderRegistered(address indexed lender, uint256 fundingCapacity);
    event CollateralDeposited(uint256 indexed loanId, address indexed borrower, uint256 amount);
    event CollateralReleased(uint256 indexed loanId, address indexed borrower, uint256 amount);

    // Enums
    enum LoanStatus { Requested, Approved, Funded, Active, Completed, Defaulted }
    enum LoanType { Personal, Business, Agricultural, Educational }

    // Structs
    struct Loan {
        uint256 loanId;
        address borrower;
        address lender;
        address token;
        uint256 principalAmount;
        uint256 interestRate; // Annual interest rate in basis points (100 = 1%)
        uint256 duration; // Loan duration in days
        uint256 installmentAmount;
        uint256 installmentsPaid;
        uint256 totalInstallments;
        uint256 startTime;
        uint256 nextPaymentDue;
        LoanStatus status;
        LoanType loanType;
        string purpose;
        uint256 collateralAmount;
        bool requiresCollateral;
        uint256 creditScoreRequired;
        uint256 totalRepaid;
        uint256 penaltyAmount;
    }

    struct Borrower {
        uint256 creditScore;
        uint256 totalBorrowed;
        uint256 totalRepaid;
        uint256 activeLoans;
        uint256 completedLoans;
        uint256 defaultedLoans;
        uint256 lastLoanTime;
        bool isBlacklisted;
    }

    struct Lender {
        bool isRegistered;
        uint256 fundingCapacity;
        uint256 totalLent;
        uint256 activeLoans;
        uint256 completedLoans;
        uint256 defaultRate; // in basis points
        uint256 averageReturn;
        address[] supportedTokens;
        mapping(address => uint256) availableFunds;
    }

    struct LoanOffer {
        address lender;
        uint256 maxAmount;
        uint256 interestRate;
        uint256 maxDuration;
        uint256 minCreditScore;
        LoanType[] supportedTypes;
        bool isActive;
    }

    // State variables
    mapping(uint256 => Loan) public loans;
    mapping(address => Borrower) public borrowers;
    mapping(address => Lender) public lenders;
    mapping(uint256 => LoanOffer) public loanOffers;
    mapping(address => uint256[]) public borrowerLoans;
    mapping(address => uint256[]) public lenderLoans;

    uint256 public nextLoanId = 1;
    uint256 public nextOfferId = 1;
    uint256 public platformFee = 200; // 2% platform fee in basis points
    uint256 public latePenaltyRate = 500; // 5% penalty for late payments
    uint256 public maxLoanAmount = 50000 * 10**18; // 50,000 tokens
    uint256 public minCreditScore = 400;
    uint256 public installmentPeriod = 30 days; // Monthly installments

    address public feeReceiver;
    address public creditScoringOracle;

    constructor(address _feeReceiver, address _creditScoringOracle) {
        feeReceiver = _feeReceiver;
        creditScoringOracle = _creditScoringOracle;
    }

    modifier onlyRegisteredLender() {
        require(lenders[msg.sender].isRegistered, "Not a registered lender");
        _;
    }

    modifier onlyCreditOracle() {
        require(msg.sender == creditScoringOracle, "Only credit oracle");
        _;
    }

    modifier loanExists(uint256 _loanId) {
        require(_loanId > 0 && _loanId < nextLoanId, "Loan does not exist");
        _;
    }

    /**
     * @dev Register as a lender
     */
    function registerLender(uint256 _fundingCapacity, address[] memory _supportedTokens) external {
        require(!lenders[msg.sender].isRegistered, "Already registered");
        require(_fundingCapacity > 0, "Invalid funding capacity");

        Lender storage lender = lenders[msg.sender];
        lender.isRegistered = true;
        lender.fundingCapacity = _fundingCapacity;
        lender.supportedTokens = _supportedTokens;

        emit LenderRegistered(msg.sender, _fundingCapacity);
    }

    /**
     * @dev Deposit funds for lending
     */
    function depositFunds(address _token, uint256 _amount) external nonReentrant onlyRegisteredLender {
        require(_amount > 0, "Amount must be greater than 0");

        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
        lenders[msg.sender].availableFunds[_token] = lenders[msg.sender].availableFunds[_token].add(_amount);
    }

    /**
     * @dev Withdraw available funds
     */
    function withdrawFunds(address _token, uint256 _amount) external nonReentrant onlyRegisteredLender {
        require(_amount > 0, "Amount must be greater than 0");
        require(lenders[msg.sender].availableFunds[_token] >= _amount, "Insufficient funds");

        lenders[msg.sender].availableFunds[_token] = lenders[msg.sender].availableFunds[_token].sub(_amount);
        IERC20(_token).safeTransfer(msg.sender, _amount);
    }

    /**
     * @dev Create a loan offer
     */
    function createLoanOffer(
        uint256 _maxAmount,
        uint256 _interestRate,
        uint256 _maxDuration,
        uint256 _minCreditScore,
        LoanType[] memory _supportedTypes
    ) external onlyRegisteredLender returns (uint256) {
        require(_maxAmount > 0 && _maxAmount <= maxLoanAmount, "Invalid loan amount");
        require(_interestRate > 0 && _interestRate <= 5000, "Invalid interest rate"); // Max 50%
        require(_maxDuration > 0 && _maxDuration <= 365, "Invalid duration");
        require(_minCreditScore >= 300 && _minCreditScore <= 850, "Invalid credit score");

        uint256 offerId = nextOfferId++;
        LoanOffer storage offer = loanOffers[offerId];

        offer.lender = msg.sender;
        offer.maxAmount = _maxAmount;
        offer.interestRate = _interestRate;
        offer.maxDuration = _maxDuration;
        offer.minCreditScore = _minCreditScore;
        offer.supportedTypes = _supportedTypes;
        offer.isActive = true;

        return offerId;
    }

    /**
     * @dev Request a loan
     */
    function requestLoan(
        address _token,
        uint256 _amount,
        uint256 _duration,
        LoanType _loanType,
        string memory _purpose,
        bool _requiresCollateral
    ) external whenNotPaused returns (uint256) {
        require(_amount > 0 && _amount <= maxLoanAmount, "Invalid loan amount");
        require(_duration > 0 && _duration <= 365, "Invalid duration");
        require(!borrowers[msg.sender].isBlacklisted, "Borrower blacklisted");

        // Check borrower's credit score
        require(borrowers[msg.sender].creditScore >= minCreditScore, "Credit score too low");

        uint256 loanId = nextLoanId++;

        // Calculate installment details
        uint256 totalInstallments = _duration.div(30).add(1); // Monthly installments
        uint256 totalAmount = _amount.add(_amount.mul(1000).div(10000)); // Add base interest
        uint256 installmentAmount = totalAmount.div(totalInstallments);

        Loan storage loan = loans[loanId];
        loan.loanId = loanId;
        loan.borrower = msg.sender;
        loan.token = _token;
        loan.principalAmount = _amount;
        loan.duration = _duration;
        loan.status = LoanStatus.Requested;
        loan.loanType = _loanType;
        loan.purpose = _purpose;
        loan.requiresCollateral = _requiresCollateral;
        loan.creditScoreRequired = minCreditScore;
        loan.totalInstallments = totalInstallments;
        loan.installmentAmount = installmentAmount;

        borrowerLoans[msg.sender].push(loanId);
        borrowers[msg.sender].activeLoans++;

        emit LoanRequested(loanId, msg.sender, _amount, _duration);
        return loanId;
    }

    /**
     * @dev Approve a loan (lender action)
     */
    function approveLoan(uint256 _loanId, uint256 _interestRate) external loanExists(_loanId) onlyRegisteredLender {
        Loan storage loan = loans[_loanId];
        require(loan.status == LoanStatus.Requested, "Loan not in requested status");
        require(_interestRate > 0 && _interestRate <= 5000, "Invalid interest rate");

        // Check if lender has sufficient funds
        require(lenders[msg.sender].availableFunds[loan.token] >= loan.principalAmount, "Insufficient lender funds");

        loan.lender = msg.sender;
        loan.interestRate = _interestRate;
        loan.status = LoanStatus.Approved;

        // Recalculate installment amount with actual interest rate
        uint256 totalInterest = loan.principalAmount.mul(_interestRate).mul(loan.duration).div(365).div(10000);
        uint256 totalAmount = loan.principalAmount.add(totalInterest);
        loan.installmentAmount = totalAmount.div(loan.totalInstallments);

        lenderLoans[msg.sender].push(_loanId);
        lenders[msg.sender].activeLoans++;

        emit LoanApproved(_loanId, msg.sender, loan.principalAmount, _interestRate);
    }

    /**
     * @dev Deposit collateral for loan
     */
    function depositCollateral(uint256 _loanId, uint256 _amount) external nonReentrant loanExists(_loanId) {
        Loan storage loan = loans[_loanId];
        require(loan.borrower == msg.sender, "Not the borrower");
        require(loan.status == LoanStatus.Approved, "Loan not approved");
        require(loan.requiresCollateral, "Collateral not required");
        require(_amount > 0, "Invalid collateral amount");

        IERC20(loan.token).safeTransferFrom(msg.sender, address(this), _amount);
        loan.collateralAmount = loan.collateralAmount.add(_amount);

        emit CollateralDeposited(_loanId, msg.sender, _amount);

        // Auto-fund loan if sufficient collateral
        if (loan.collateralAmount >= loan.principalAmount.mul(120).div(100)) { // 120% collateral
            _fundLoan(_loanId);
        }
    }

    /**
     * @dev Fund the loan (transfer money to borrower)
     */
    function fundLoan(uint256 _loanId) external loanExists(_loanId) {
        Loan storage loan = loans[_loanId];
        require(loan.lender == msg.sender, "Not the lender");
        require(loan.status == LoanStatus.Approved, "Loan not approved");

        _fundLoan(_loanId);
    }

    /**
     * @dev Internal function to fund loan
     */
    function _fundLoan(uint256 _loanId) internal {
        Loan storage loan = loans[_loanId];

        // Deduct funds from lender's available balance
        lenders[loan.lender].availableFunds[loan.token] = 
            lenders[loan.lender].availableFunds[loan.token].sub(loan.principalAmount);

        // Calculate platform fee
        uint256 fee = loan.principalAmount.mul(platformFee).div(10000);
        uint256 amountToBorrower = loan.principalAmount.sub(fee);

        // Transfer funds
        IERC20(loan.token).safeTransfer(loan.borrower, amountToBorrower);
        if (fee > 0) {
            IERC20(loan.token).safeTransfer(feeReceiver, fee);
        }

        // Update loan status
        loan.status = LoanStatus.Active;
        loan.startTime = block.timestamp;
        loan.nextPaymentDue = block.timestamp.add(installmentPeriod);

        // Update borrower stats
        borrowers[loan.borrower].totalBorrowed = borrowers[loan.borrower].totalBorrowed.add(loan.principalAmount);
        borrowers[loan.borrower].lastLoanTime = block.timestamp;

        // Update lender stats
        lenders[loan.lender].totalLent = lenders[loan.lender].totalLent.add(loan.principalAmount);

        emit LoanFunded(_loanId, amountToBorrower);
    }

    /**
     * @dev Make loan repayment
     */
    function makeRepayment(uint256 _loanId) external nonReentrant loanExists(_loanId) {
        Loan storage loan = loans[_loanId];
        require(loan.borrower == msg.sender, "Not the borrower");
        require(loan.status == LoanStatus.Active, "Loan not active");
        require(loan.installmentsPaid < loan.totalInstallments, "Loan already completed");

        uint256 paymentAmount = loan.installmentAmount;

        // Add penalty if payment is late
        if (block.timestamp > loan.nextPaymentDue) {
            uint256 daysLate = (block.timestamp - loan.nextPaymentDue) / 86400;
            uint256 penalty = loan.installmentAmount.mul(latePenaltyRate).mul(daysLate).div(10000).div(365);
            paymentAmount = paymentAmount.add(penalty);
            loan.penaltyAmount = loan.penaltyAmount.add(penalty);
        }

        // Transfer payment
        IERC20(loan.token).safeTransferFrom(msg.sender, address(this), paymentAmount);

        // Distribute payment (90% to lender, 10% platform fee on interest portion)
        uint256 principalPortion = loan.principalAmount.div(loan.totalInstallments);
        uint256 interestPortion = paymentAmount.sub(principalPortion);
        uint256 platformFeeAmount = interestPortion.mul(1000).div(10000); // 10% of interest
        uint256 lenderAmount = paymentAmount.sub(platformFeeAmount);

        lenders[loan.lender].availableFunds[loan.token] = 
            lenders[loan.lender].availableFunds[loan.token].add(lenderAmount);

        if (platformFeeAmount > 0) {
            IERC20(loan.token).safeTransfer(feeReceiver, platformFeeAmount);
        }

        // Update loan details
        loan.installmentsPaid++;
        loan.totalRepaid = loan.totalRepaid.add(paymentAmount);
        loan.nextPaymentDue = loan.nextPaymentDue.add(installmentPeriod);

        // Update borrower stats
        borrowers[loan.borrower].totalRepaid = borrowers[loan.borrower].totalRepaid.add(paymentAmount);

        emit RepaymentMade(_loanId, msg.sender, paymentAmount, loan.installmentsPaid);

        // Check if loan is completed
        if (loan.installmentsPaid >= loan.totalInstallments) {
            _completeLoan(_loanId);
        }
    }

    /**
     * @dev Complete loan and release collateral
     */
    function _completeLoan(uint256 _loanId) internal {
        Loan storage loan = loans[_loanId];

        loan.status = LoanStatus.Completed;

        // Release collateral if any
        if (loan.collateralAmount > 0) {
            IERC20(loan.token).safeTransfer(loan.borrower, loan.collateralAmount);
            emit CollateralReleased(_loanId, loan.borrower, loan.collateralAmount);
        }

        // Update stats
        borrowers[loan.borrower].activeLoans--;
        borrowers[loan.borrower].completedLoans++;
        lenders[loan.lender].activeLoans--;
        lenders[loan.lender].completedLoans++;

        // Improve credit score for successful completion
        _updateCreditScore(loan.borrower, 10);

        emit LoanCompleted(_loanId, loan.borrower);
    }

    /**
     * @dev Mark loan as defaulted (can be called by lender or oracle)
     */
    function markLoanDefaulted(uint256 _loanId) external loanExists(_loanId) {
        Loan storage loan = loans[_loanId];
        require(
            msg.sender == loan.lender || 
            msg.sender == owner() || 
            msg.sender == creditScoringOracle, 
            "Not authorized"
        );
        require(loan.status == LoanStatus.Active, "Loan not active");
        require(block.timestamp > loan.nextPaymentDue.add(7 days), "Grace period not over");

        loan.status = LoanStatus.Defaulted;

        // Transfer collateral to lender if available
        if (loan.collateralAmount > 0) {
            IERC20(loan.token).safeTransfer(loan.lender, loan.collateralAmount);
        }

        // Update stats
        borrowers[loan.borrower].activeLoans--;
        borrowers[loan.borrower].defaultedLoans++;
        lenders[loan.lender].activeLoans--;

        // Severely impact credit score
        _updateCreditScore(loan.borrower, -50);

        // Blacklist borrower if multiple defaults
        if (borrowers[loan.borrower].defaultedLoans >= 2) {
            borrowers[loan.borrower].isBlacklisted = true;
        }

        emit LoanDefaulted(_loanId, loan.borrower);
    }

    /**
     * @dev Update credit score (only oracle or automated)
     */
    function updateCreditScore(address _borrower, uint256 _newScore) external onlyCreditOracle {
        require(_newScore >= 300 && _newScore <= 850, "Invalid credit score");
        borrowers[_borrower].creditScore = _newScore;
    }

    /**
     * @dev Internal function to update credit score
     */
    function _updateCreditScore(address _borrower, int256 _change) internal {
        Borrower storage borrower = borrowers[_borrower];

        if (_change > 0) {
            borrower.creditScore = borrower.creditScore.add(uint256(_change));
            if (borrower.creditScore > 850) borrower.creditScore = 850;
        } else {
            uint256 decrease = uint256(-_change);
            if (decrease >= borrower.creditScore) {
                borrower.creditScore = 300; // Minimum score
            } else {
                borrower.creditScore = borrower.creditScore.sub(decrease);
            }
        }
    }

    /**
     * @dev Get loan details
     */
    function getLoanDetails(uint256 _loanId) external view loanExists(_loanId) returns (
        address borrower,
        address lender,
        uint256 principalAmount,
        uint256 interestRate,
        uint256 installmentAmount,
        uint256 installmentsPaid,
        uint256 totalInstallments,
        LoanStatus status,
        uint256 nextPaymentDue
    ) {
        Loan storage loan = loans[_loanId];
        return (
            loan.borrower,
            loan.lender,
            loan.principalAmount,
            loan.interestRate,
            loan.installmentAmount,
            loan.installmentsPaid,
            loan.totalInstallments,
            loan.status,
            loan.nextPaymentDue
        );
    }

    /**
     * @dev Get borrower information
     */
    function getBorrowerInfo(address _borrower) external view returns (
        uint256 creditScore,
        uint256 totalBorrowed,
        uint256 totalRepaid,
        uint256 activeLoans,
        uint256 completedLoans,
        uint256 defaultedLoans,
        bool isBlacklisted
    ) {
        Borrower storage borrower = borrowers[_borrower];
        return (
            borrower.creditScore,
            borrower.totalBorrowed,
            borrower.totalRepaid,
            borrower.activeLoans,
            borrower.completedLoans,
            borrower.defaultedLoans,
            borrower.isBlacklisted
        );
    }

    /**
     * @dev Get lender available funds
     */
    function getLenderFunds(address _lender, address _token) external view returns (uint256) {
        return lenders[_lender].availableFunds[_token];
    }

    /**
     * @dev Update platform settings (only owner)
     */
    function updateSettings(
        uint256 _platformFee,
        uint256 _latePenaltyRate,
        uint256 _maxLoanAmount,
        uint256 _minCreditScore
    ) external onlyOwner {
        require(_platformFee <= 1000, "Platform fee too high");
        require(_latePenaltyRate <= 2000, "Penalty rate too high");

        platformFee = _platformFee;
        latePenaltyRate = _latePenaltyRate;
        maxLoanAmount = _maxLoanAmount;
        minCreditScore = _minCreditScore;
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/SavingsGroups.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title SavingsGroups
 * @dev Smart contract for managing ROSCA (Rotating Savings and Credit Associations)
 * Traditional community savings groups enhanced with blockchain transparency
 */
contract SavingsGroups is ReentrancyGuard, Pausable, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Events
    event GroupCreated(uint256 indexed groupId, address indexed creator, string name, uint256 contributionAmount);
    event MemberJoined(uint256 indexed groupId, address indexed member, uint256 position);
    event ContributionMade(uint256 indexed groupId, address indexed member, uint256 amount, uint256 round);
    event PayoutDistributed(uint256 indexed groupId, address indexed recipient, uint256 amount, uint256 round);
    event GroupCompleted(uint256 indexed groupId, uint256 totalRounds);
    event EmergencyLoanRequested(uint256 indexed groupId, address indexed member, uint256 amount);
    event EmergencyLoanApproved(uint256 indexed groupId, address indexed member, uint256 amount);

    // Enums
    enum GroupStatus { Active, Completed, Cancelled }
    enum ContributionFrequency { Daily, Weekly, Monthly }

    // Structs
    struct SavingsGroup {
        uint256 groupId;
        string name;
        string description;
        address creator;
        address token; // ERC20 token used for contributions
        uint256 contributionAmount;
        uint256 maxMembers;
        uint256 currentMembers;
        ContributionFrequency frequency;
        uint256 startTime;
        uint256 lastPayoutTime;
        uint256 currentRound;
        GroupStatus status;
        bool emergencyFundEnabled;
        uint256 emergencyFundBalance;
        uint256 totalContributions;
        mapping(address => Member) members;
        mapping(uint256 => address) payoutOrder; // round => member address
        address[] memberList;
    }

    struct Member {
        bool isActive;
        uint256 joinTime;
        uint256 position; // position in payout order
        uint256 totalContributions;
        uint256 missedContributions;
        uint256 lastContributionTime;
        bool hasReceivedPayout;
        uint256 payoutRound;
        uint256 emergencyLoanAmount;
        bool hasEmergencyLoan;
    }

    struct EmergencyLoan {
        uint256 groupId;
        address borrower;
        uint256 amount;
        uint256 requestTime;
        uint256 approvalCount;
        bool isApproved;
        bool isRepaid;
        mapping(address => bool) approvals;
    }

    // State variables
    mapping(uint256 => SavingsGroup) public savingsGroups;
    mapping(uint256 => EmergencyLoan) public emergencyLoans;
    mapping(address => uint256[]) public userGroups; // user => array of group IDs

    uint256 public nextGroupId = 1;
    uint256 public nextLoanId = 1;
    uint256 public platformFee = 25; // 0.25% in basis points
    address public feeReceiver;

    // Group creation settings
    uint256 public minimumContribution = 1000 * 10**18; // 1000 tokens minimum
    uint256 public maximumMembers = 50;
    uint256 public emergencyFundPercentage = 500; // 5% of contributions go to emergency fund

    constructor(address _feeReceiver) {
        feeReceiver = _feeReceiver;
    }

    modifier onlyGroupMember(uint256 _groupId) {
        require(savingsGroups[_groupId].members[msg.sender].isActive, "Not a group member");
        _;
    }

    modifier onlyGroupCreator(uint256 _groupId) {
        require(savingsGroups[_groupId].creator == msg.sender, "Not group creator");
        _;
    }

    modifier groupExists(uint256 _groupId) {
        require(_groupId > 0 && _groupId < nextGroupId, "Group does not exist");
        _;
    }

    /**
     * @dev Create a new savings group
     */
    function createGroup(
        string memory _name,
        string memory _description,
        address _token,
        uint256 _contributionAmount,
        uint256 _maxMembers,
        ContributionFrequency _frequency,
        bool _emergencyFundEnabled
    ) external whenNotPaused returns (uint256) {
        require(bytes(_name).length > 0, "Group name required");
        require(_contributionAmount >= minimumContribution, "Contribution too low");
        require(_maxMembers >= 3 && _maxMembers <= maximumMembers, "Invalid member count");
        require(_token != address(0), "Invalid token address");

        uint256 groupId = nextGroupId++;
        SavingsGroup storage group = savingsGroups[groupId];

        group.groupId = groupId;
        group.name = _name;
        group.description = _description;
        group.creator = msg.sender;
        group.token = _token;
        group.contributionAmount = _contributionAmount;
        group.maxMembers = _maxMembers;
        group.currentMembers = 1;
        group.frequency = _frequency;
        group.startTime = block.timestamp;
        group.status = GroupStatus.Active;
        group.emergencyFundEnabled = _emergencyFundEnabled;

        // Add creator as first member
        group.members[msg.sender] = Member({
            isActive: true,
            joinTime: block.timestamp,
            position: 1,
            totalContributions: 0,
            missedContributions: 0,
            lastContributionTime: 0,
            hasReceivedPayout: false,
            payoutRound: 0,
            emergencyLoanAmount: 0,
            hasEmergencyLoan: false
        });

        group.memberList.push(msg.sender);
        group.payoutOrder[1] = msg.sender; // Creator gets first payout
        userGroups[msg.sender].push(groupId);

        emit GroupCreated(groupId, msg.sender, _name, _contributionAmount);
        return groupId;
    }

    /**
     * @dev Join an existing savings group
     */
    function joinGroup(uint256 _groupId) external whenNotPaused groupExists(_groupId) {
        SavingsGroup storage group = savingsGroups[_groupId];

        require(group.status == GroupStatus.Active, "Group not active");
        require(!group.members[msg.sender].isActive, "Already a member");
        require(group.currentMembers < group.maxMembers, "Group is full");

        group.currentMembers++;
        uint256 position = group.currentMembers;

        group.members[msg.sender] = Member({
            isActive: true,
            joinTime: block.timestamp,
            position: position,
            totalContributions: 0,
            missedContributions: 0,
            lastContributionTime: 0,
            hasReceivedPayout: false,
            payoutRound: 0,
            emergencyLoanAmount: 0,
            hasEmergencyLoan: false
        });

        group.memberList.push(msg.sender);
        group.payoutOrder[position] = msg.sender;
        userGroups[msg.sender].push(_groupId);

        emit MemberJoined(_groupId, msg.sender, position);
    }

    /**
     * @dev Make a contribution to the savings group
     */
    function contribute(uint256 _groupId) external nonReentrant whenNotPaused onlyGroupMember(_groupId) {
        SavingsGroup storage group = savingsGroups[_groupId];
        require(group.status == GroupStatus.Active, "Group not active");

        uint256 amount = group.contributionAmount;
        uint256 emergencyFundAmount = 0;

        if (group.emergencyFundEnabled) {
            emergencyFundAmount = amount.mul(emergencyFundPercentage).div(10000);
            amount = amount.sub(emergencyFundAmount);
            group.emergencyFundBalance = group.emergencyFundBalance.add(emergencyFundAmount);
        }

        // Transfer tokens from user
        IERC20(group.token).safeTransferFrom(msg.sender, address(this), group.contributionAmount);

        // Update member contribution data
        Member storage member = group.members[msg.sender];
        member.totalContributions = member.totalContributions.add(group.contributionAmount);
        member.lastContributionTime = block.timestamp;

        // Update group totals
        group.totalContributions = group.totalContributions.add(amount);

        emit ContributionMade(_groupId, msg.sender, group.contributionAmount, group.currentRound);

        // Check if it's time for payout
        _checkAndProcessPayout(_groupId);
    }

    /**
     * @dev Process payout for the current round
     */
    function _checkAndProcessPayout(uint256 _groupId) internal {
        SavingsGroup storage group = savingsGroups[_groupId];

        // Check if all members have contributed for this round
        uint256 contributionsThisRound = 0;
        for (uint256 i = 0; i < group.memberList.length; i++) {
            address memberAddr = group.memberList[i];
            if (_hasContributedThisRound(_groupId, memberAddr)) {
                contributionsThisRound++;
            }
        }

        if (contributionsThisRound == group.currentMembers) {
            _processPayout(_groupId);
        }
    }

    /**
     * @dev Process payout to the next member in line
     */
    function _processPayout(uint256 _groupId) internal {
        SavingsGroup storage group = savingsGroups[_groupId];

        group.currentRound++;
        address payoutRecipient = group.payoutOrder[group.currentRound];

        require(payoutRecipient != address(0), "Invalid payout recipient");

        uint256 payoutAmount = group.totalContributions;

        // Calculate and deduct platform fee
        uint256 fee = payoutAmount.mul(platformFee).div(10000);
        uint256 finalPayout = payoutAmount.sub(fee);

        // Transfer payout to recipient
        IERC20(group.token).safeTransfer(payoutRecipient, finalPayout);

        // Transfer fee to platform
        if (fee > 0) {
            IERC20(group.token).safeTransfer(feeReceiver, fee);
        }

        // Update member payout status
        Member storage member = group.members[payoutRecipient];
        member.hasReceivedPayout = true;
        member.payoutRound = group.currentRound;

        // Reset total contributions for next round
        group.totalContributions = 0;
        group.lastPayoutTime = block.timestamp;

        emit PayoutDistributed(_groupId, payoutRecipient, finalPayout, group.currentRound);

        // Check if group is completed
        if (group.currentRound >= group.currentMembers) {
            group.status = GroupStatus.Completed;
            emit GroupCompleted(_groupId, group.currentRound);
        }
    }

    /**
     * @dev Check if member has contributed in current round
     */
    function _hasContributedThisRound(uint256 _groupId, address _member) internal view returns (bool) {
        SavingsGroup storage group = savingsGroups[_groupId];
        Member storage member = group.members[_member];

        uint256 frequencyInSeconds = _getFrequencyInSeconds(group.frequency);
        uint256 roundStartTime = group.lastPayoutTime > 0 ? group.lastPayoutTime : group.startTime;

        return member.lastContributionTime >= roundStartTime && 
               member.lastContributionTime < roundStartTime.add(frequencyInSeconds);
    }

    /**
     * @dev Get frequency in seconds
     */
    function _getFrequencyInSeconds(ContributionFrequency _frequency) internal pure returns (uint256) {
        if (_frequency == ContributionFrequency.Daily) return 86400; // 1 day
        if (_frequency == ContributionFrequency.Weekly) return 604800; // 7 days
        if (_frequency == ContributionFrequency.Monthly) return 2592000; // 30 days
        return 604800; // Default to weekly
    }

    /**
     * @dev Request emergency loan from group fund
     */
    function requestEmergencyLoan(uint256 _groupId, uint256 _amount, string memory _reason) 
        external 
        whenNotPaused 
        onlyGroupMember(_groupId) 
        returns (uint256) 
    {
        SavingsGroup storage group = savingsGroups[_groupId];
        require(group.emergencyFundEnabled, "Emergency fund not enabled");
        require(_amount <= group.emergencyFundBalance, "Insufficient emergency fund");
        require(!group.members[msg.sender].hasEmergencyLoan, "Already has active loan");

        uint256 loanId = nextLoanId++;
        EmergencyLoan storage loan = emergencyLoans[loanId];

        loan.groupId = _groupId;
        loan.borrower = msg.sender;
        loan.amount = _amount;
        loan.requestTime = block.timestamp;
        loan.approvalCount = 0;
        loan.isApproved = false;
        loan.isRepaid = false;

        emit EmergencyLoanRequested(_groupId, msg.sender, _amount);
        return loanId;
    }

    /**
     * @dev Approve emergency loan (requires majority approval)
     */
    function approveEmergencyLoan(uint256 _loanId) external {
        EmergencyLoan storage loan = emergencyLoans[_loanId];
        require(loan.borrower != address(0), "Loan does not exist");
        require(!loan.approvals[msg.sender], "Already approved");

        SavingsGroup storage group = savingsGroups[loan.groupId];
        require(group.members[msg.sender].isActive, "Not a group member");
        require(!loan.isApproved, "Loan already approved");

        loan.approvals[msg.sender] = true;
        loan.approvalCount++;

        // Check if majority approval reached (>50% of members)
        uint256 requiredApprovals = group.currentMembers.div(2).add(1);
        if (loan.approvalCount >= requiredApprovals) {
            _processEmergencyLoan(_loanId);
        }
    }

    /**
     * @dev Process emergency loan disbursement
     */
    function _processEmergencyLoan(uint256 _loanId) internal {
        EmergencyLoan storage loan = emergencyLoans[_loanId];
        SavingsGroup storage group = savingsGroups[loan.groupId];

        loan.isApproved = true;
        group.emergencyFundBalance = group.emergencyFundBalance.sub(loan.amount);
        group.members[loan.borrower].hasEmergencyLoan = true;
        group.members[loan.borrower].emergencyLoanAmount = loan.amount;

        // Transfer loan amount to borrower
        IERC20(group.token).safeTransfer(loan.borrower, loan.amount);

        emit EmergencyLoanApproved(loan.groupId, loan.borrower, loan.amount);
    }

    /**
     * @dev Repay emergency loan
     */
    function repayEmergencyLoan(uint256 _loanId) external nonReentrant whenNotPaused {
        EmergencyLoan storage loan = emergencyLoans[_loanId];
        require(loan.borrower == msg.sender, "Not the borrower");
        require(loan.isApproved, "Loan not approved");
        require(!loan.isRepaid, "Loan already repaid");

        SavingsGroup storage group = savingsGroups[loan.groupId];

        // Transfer repayment amount
        IERC20(group.token).safeTransferFrom(msg.sender, address(this), loan.amount);

        // Update loan and member status
        loan.isRepaid = true;
        group.emergencyFundBalance = group.emergencyFundBalance.add(loan.amount);
        group.members[msg.sender].hasEmergencyLoan = false;
        group.members[msg.sender].emergencyLoanAmount = 0;
    }

    /**
     * @dev Get group information
     */
    function getGroupInfo(uint256 _groupId) external view groupExists(_groupId) returns (
        string memory name,
        string memory description,
        address creator,
        address token,
        uint256 contributionAmount,
        uint256 currentMembers,
        uint256 maxMembers,
        GroupStatus status,
        uint256 currentRound,
        uint256 emergencyFundBalance
    ) {
        SavingsGroup storage group = savingsGroups[_groupId];
        return (
            group.name,
            group.description,
            group.creator,
            group.token,
            group.contributionAmount,
            group.currentMembers,
            group.maxMembers,
            group.status,
            group.currentRound,
            group.emergencyFundBalance
        );
    }

    /**
     * @dev Get member information
     */
    function getMemberInfo(uint256 _groupId, address _member) external view groupExists(_groupId) returns (
        bool isActive,
        uint256 position,
        uint256 totalContributions,
        uint256 missedContributions,
        bool hasReceivedPayout,
        uint256 payoutRound
    ) {
        Member storage member = savingsGroups[_groupId].members[_member];
        return (
            member.isActive,
            member.position,
            member.totalContributions,
            member.missedContributions,
            member.hasReceivedPayout,
            member.payoutRound
        );
    }

    /**
     * @dev Get user's groups
     */
    function getUserGroups(address _user) external view returns (uint256[] memory) {
        return userGroups[_user];
    }

    /**
     * @dev Update platform fee (only owner)
     */
    function updatePlatformFee(uint256 _platformFee) external onlyOwner {
        require(_platformFee <= 1000, "Platform fee too high"); // Max 10%
        platformFee = _platformFee;
    }

    /**
     * @dev Update emergency fund percentage (only owner)
     */
    function updateEmergencyFundPercentage(uint256 _percentage) external onlyOwner {
        require(_percentage <= 1000, "Percentage too high"); // Max 10%
        emergencyFundPercentage = _percentage;
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/VillageToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";

/**
 * @title VillageToken
 * @dev ERC20 token for Village Digital Wallet rewards and governance
 */
contract VillageToken is ERC20, ERC20Burnable, Pausable, Ownable, ERC20Permit {

    uint256 public constant MAX_SUPPLY = 1000000000 * 10**18; // 1 billion tokens
    uint256 public constant INITIAL_SUPPLY = 100000000 * 10**18; // 100 million initial

    // Reward distribution rates (in tokens per action)
    uint256 public savingsReward = 10 * 10**18; // 10 tokens per savings contribution
    uint256 public loanRepaymentReward = 5 * 10**18; // 5 tokens per loan repayment
    uint256 public referralReward = 25 * 10**18; // 25 tokens per referral
    uint256 public agentCommissionReward = 2 * 10**18; // 2 tokens per transaction

    // Reward pools
    mapping(string => uint256) public rewardPools;
    mapping(address => bool) public rewardDistributors;

    // Staking for governance
    mapping(address => uint256) public stakedBalance;
    mapping(address => uint256) public stakingTimestamp;
    uint256 public totalStaked;
    uint256 public stakingRewardRate = 100; // 1% annual reward in basis points

    // Events
    event RewardDistributed(address indexed recipient, uint256 amount, string rewardType);
    event TokensStaked(address indexed user, uint256 amount);
    event TokensUnstaked(address indexed user, uint256 amount);
    event StakingRewardClaimed(address indexed user, uint256 amount);

    constructor() 
        ERC20("Village Token", "VILLAGE") 
        ERC20Permit("Village Token")
    {
        _mint(msg.sender, INITIAL_SUPPLY);

        // Initialize reward pools
        rewardPools["savings"] = 50000000 * 10**18; // 50M for savings rewards
        rewardPools["loans"] = 30000000 * 10**18; // 30M for loan rewards
        rewardPools["referrals"] = 15000000 * 10**18; // 15M for referrals
        rewardPools["agents"] = 20000000 * 10**18; // 20M for agent rewards
        rewardPools["staking"] = 10000000 * 10**18; // 10M for staking rewards
    }

    modifier onlyRewardDistributor() {
        require(rewardDistributors[msg.sender], "Not authorized to distribute rewards");
        _;
    }

    /**
     * @dev Add authorized reward distributor
     */
    function addRewardDistributor(address _distributor) external onlyOwner {
        rewardDistributors[_distributor] = true;
    }

    /**
     * @dev Remove reward distributor
     */
    function removeRewardDistributor(address _distributor) external onlyOwner {
        rewardDistributors[_distributor] = false;
    }

    /**
     * @dev Distribute savings reward
     */
    function distributeSavingsReward(address _recipient) external onlyRewardDistributor {
        require(rewardPools["savings"] >= savingsReward, "Insufficient savings reward pool");

        rewardPools["savings"] -= savingsReward;
        _mint(_recipient, savingsReward);

        emit RewardDistributed(_recipient, savingsReward, "savings");
    }

    /**
     * @dev Distribute loan repayment reward
     */
    function distributeLoanReward(address _recipient) external onlyRewardDistributor {
        require(rewardPools["loans"] >= loanRepaymentReward, "Insufficient loan reward pool");

        rewardPools["loans"] -= loanRepaymentReward;
        _mint(_recipient, loanRepaymentReward);

        emit RewardDistributed(_recipient, loanRepaymentReward, "loan_repayment");
    }

    /**
     * @dev Distribute referral reward
     */
    function distributeReferralReward(address _recipient) external onlyRewardDistributor {
        require(rewardPools["referrals"] >= referralReward, "Insufficient referral reward pool");

        rewardPools["referrals"] -= referralReward;
        _mint(_recipient, referralReward);

        emit RewardDistributed(_recipient, referralReward, "referral");
    }

    /**
     * @dev Distribute agent commission reward
     */
    function distributeAgentReward(address _recipient) external onlyRewardDistributor {
        require(rewardPools["agents"] >= agentCommissionReward, "Insufficient agent reward pool");

        rewardPools["agents"] -= agentCommissionReward;
        _mint(_recipient, agentCommissionReward);

        emit RewardDistributed(_recipient, agentCommissionReward, "agent_commission");
    }

    /**
     * @dev Stake tokens for governance voting
     */
    function stakeTokens(uint256 _amount) external whenNotPaused {
        require(_amount > 0, "Amount must be greater than 0");
        require(balanceOf(msg.sender) >= _amount, "Insufficient balance");

        // Claim any pending staking rewards before staking more
        if (stakedBalance[msg.sender] > 0) {
            _claimStakingReward();
        }

        _transfer(msg.sender, address(this), _amount);
        stakedBalance[msg.sender] += _amount;
        stakingTimestamp[msg.sender] = block.timestamp;
        totalStaked += _amount;

        emit TokensStaked(msg.sender, _amount);
    }

    /**
     * @dev Unstake tokens
     */
    function unstakeTokens(uint256 _amount) external whenNotPaused {
        require(_amount > 0, "Amount must be greater than 0");
        require(stakedBalance[msg.sender] >= _amount, "Insufficient staked balance");
        require(block.timestamp >= stakingTimestamp[msg.sender] + 7 days, "Tokens locked for 7 days");

        // Claim staking rewards before unstaking
        _claimStakingReward();

        stakedBalance[msg.sender] -= _amount;
        totalStaked -= _amount;
        _transfer(address(this), msg.sender, _amount);

        // Update staking timestamp for remaining tokens
        if (stakedBalance[msg.sender] > 0) {
            stakingTimestamp[msg.sender] = block.timestamp;
        }

        emit TokensUnstaked(msg.sender, _amount);
    }

    /**
     * @dev Claim staking rewards
     */
    function claimStakingReward() external whenNotPaused {
        require(stakedBalance[msg.sender] > 0, "No tokens staked");
        _claimStakingReward();
    }

    /**
     * @dev Internal function to calculate and distribute staking rewards
     */
    function _claimStakingReward() internal {
        uint256 stakingDuration = block.timestamp - stakingTimestamp[msg.sender];
        uint256 reward = (stakedBalance[msg.sender] * stakingRewardRate * stakingDuration) / (365 days * 10000);

        if (reward > 0 && reward <= rewardPools["staking"]) {
            rewardPools["staking"] -= reward;
            _mint(msg.sender, reward);
            stakingTimestamp[msg.sender] = block.timestamp;

            emit StakingRewardClaimed(msg.sender, reward);
        }
    }

    /**
     * @dev Get pending staking reward
     */
    function getPendingStakingReward(address _user) external view returns (uint256) {
        if (stakedBalance[_user] == 0) return 0;

        uint256 stakingDuration = block.timestamp - stakingTimestamp[_user];
        return (stakedBalance[_user] * stakingRewardRate * stakingDuration) / (365 days * 10000);
    }

    /**
     * @dev Get voting power (staked tokens)
     */
    function getVotingPower(address _user) external view returns (uint256) {
        return stakedBalance[_user];
    }

    /**
     * @dev Update reward rates (only owner)
     */
    function updateRewardRates(
        uint256 _savingsReward,
        uint256 _loanRepaymentReward,
        uint256 _referralReward,
        uint256 _agentCommissionReward
    ) external onlyOwner {
        savingsReward = _savingsReward;
        loanRepaymentReward = _loanRepaymentReward;
        referralReward = _referralReward;
        agentCommissionReward = _agentCommissionReward;
    }

    /**
     * @dev Update staking reward rate (only owner)
     */
    function updateStakingRewardRate(uint256 _stakingRewardRate) external onlyOwner {
        require(_stakingRewardRate <= 2000, "Staking reward rate too high"); // Max 20%
        stakingRewardRate = _stakingRewardRate;
    }

    /**
     * @dev Add tokens to reward pool (only owner)
     */
    function addToRewardPool(string memory _poolName, uint256 _amount) external onlyOwner {
        require(totalSupply() + _amount <= MAX_SUPPLY, "Would exceed max supply");
        rewardPools[_poolName] += _amount;
        _mint(address(this), _amount);
    }

    /**
     * @dev Mint tokens for ecosystem growth (only owner, limited)
     */
    function mintForEcosystem(address _recipient, uint256 _amount) external onlyOwner {
        require(totalSupply() + _amount <= MAX_SUPPLY, "Would exceed max supply");
        _mint(_recipient, _amount);
    }

    /**
     * @dev Override transfer to handle staked tokens
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @dev Pause token transfers (only owner)
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers (only owner)
     */
    function unpause() public onlyOwner {
        _unpause();
    }
}

contracts/VillageWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title VillageWallet
 * @dev Main contract for Village Digital Wallet system
 * Manages user accounts, balances, and core wallet functionality
 */
contract VillageWallet is ReentrancyGuard, Pausable, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Events
    event UserRegistered(address indexed user, string phoneNumber, string country);
    event DepositMade(address indexed user, address indexed token, uint256 amount);
    event WithdrawalMade(address indexed user, address indexed token, uint256 amount);
    event TransferMade(address indexed from, address indexed to, address indexed token, uint256 amount);
    event AgentRegistered(address indexed agent, string location, uint256 commission);
    event CashInOutCompleted(address indexed user, address indexed agent, uint256 amount, bool isCashIn);

    // Structs
    struct User {
        string phoneNumber;
        string country;
        bool isRegistered;
        bool isVerified;
        uint256 registrationTime;
        uint256 totalTransactions;
        uint256 creditScore;
    }

    struct Agent {
        string location;
        string businessName;
        uint256 commissionRate; // in basis points (100 = 1%)
        bool isActive;
        uint256 totalVolume;
        uint256 successfulTransactions;
        uint256 registrationTime;
    }

    struct Balance {
        uint256 available;
        uint256 locked; // for pending transactions
    }

    // State variables
    mapping(address => User) public users;
    mapping(address => Agent) public agents;
    mapping(address => mapping(address => Balance)) public balances; // user => token => balance
    mapping(string => address) public phoneToAddress; // phone number to address mapping

    // Supported tokens
    mapping(address => bool) public supportedTokens;
    address[] public tokenList;

    // Platform settings
    uint256 public platformFee = 50; // 0.5% in basis points
    uint256 public agentCommission = 100; // 1% in basis points
    address public feeReceiver;

    // Transaction limits
    uint256 public dailyLimit = 10000 * 10**18; // 10,000 tokens
    uint256 public monthlyLimit = 100000 * 10**18; // 100,000 tokens
    mapping(address => mapping(uint256 => uint256)) public dailySpent; // user => day => amount
    mapping(address => mapping(uint256 => uint256)) public monthlySpent; // user => month => amount

    constructor(address _feeReceiver) {
        feeReceiver = _feeReceiver;
    }

    modifier onlyRegisteredUser() {
        require(users[msg.sender].isRegistered, "User not registered");
        _;
    }

    modifier onlyVerifiedUser() {
        require(users[msg.sender].isVerified, "User not verified");
        _;
    }

    modifier onlyActiveAgent() {
        require(agents[msg.sender].isActive, "Agent not active");
        _;
    }

    modifier supportedToken(address token) {
        require(supportedTokens[token], "Token not supported");
        _;
    }

    /**
     * @dev Register a new user
     */
    function registerUser(string memory _phoneNumber, string memory _country) external {
        require(!users[msg.sender].isRegistered, "User already registered");
        require(phoneToAddress[_phoneNumber] == address(0), "Phone number already registered");

        users[msg.sender] = User({
            phoneNumber: _phoneNumber,
            country: _country,
            isRegistered: true,
            isVerified: false,
            registrationTime: block.timestamp,
            totalTransactions: 0,
            creditScore: 500 // Starting credit score
        });

        phoneToAddress[_phoneNumber] = msg.sender;

        emit UserRegistered(msg.sender, _phoneNumber, _country);
    }

    /**
     * @dev Verify user (only owner can verify)
     */
    function verifyUser(address _user) external onlyOwner {
        require(users[_user].isRegistered, "User not registered");
        users[_user].isVerified = true;
    }

    /**
     * @dev Register a new agent
     */
    function registerAgent(
        string memory _location,
        string memory _businessName,
        uint256 _commissionRate
    ) external {
        require(!agents[msg.sender].isActive, "Agent already registered");
        require(_commissionRate <= 1000, "Commission rate too high"); // Max 10%

        agents[msg.sender] = Agent({
            location: _location,
            businessName: _businessName,
            commissionRate: _commissionRate,
            isActive: true,
            totalVolume: 0,
            successfulTransactions: 0,
            registrationTime: block.timestamp
        });

        emit AgentRegistered(msg.sender, _location, _commissionRate);
    }

    /**
     * @dev Add supported token (only owner)
     */
    function addSupportedToken(address _token) external onlyOwner {
        require(!supportedTokens[_token], "Token already supported");
        supportedTokens[_token] = true;
        tokenList.push(_token);
    }

    /**
     * @dev Remove supported token (only owner)
     */
    function removeSupportedToken(address _token) external onlyOwner {
        require(supportedTokens[_token], "Token not supported");
        supportedTokens[_token] = false;

        // Remove from tokenList
        for (uint i = 0; i < tokenList.length; i++) {
            if (tokenList[i] == _token) {
                tokenList[i] = tokenList[tokenList.length - 1];
                tokenList.pop();
                break;
            }
        }
    }

    /**
     * @dev Deposit tokens to wallet
     */
    function deposit(address _token, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyRegisteredUser 
        supportedToken(_token) 
    {
        require(_amount > 0, "Amount must be greater than 0");

        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
        balances[msg.sender][_token].available = balances[msg.sender][_token].available.add(_amount);

        emit DepositMade(msg.sender, _token, _amount);
    }

    /**
     * @dev Withdraw tokens from wallet
     */
    function withdraw(address _token, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyVerifiedUser 
        supportedToken(_token) 
    {
        require(_amount > 0, "Amount must be greater than 0");
        require(balances[msg.sender][_token].available >= _amount, "Insufficient balance");
        require(_checkLimits(msg.sender, _amount), "Transaction limit exceeded");

        balances[msg.sender][_token].available = balances[msg.sender][_token].available.sub(_amount);

        // Calculate and deduct platform fee
        uint256 fee = _amount.mul(platformFee).div(10000);
        uint256 amountAfterFee = _amount.sub(fee);

        IERC20(_token).safeTransfer(msg.sender, amountAfterFee);
        if (fee > 0) {
            IERC20(_token).safeTransfer(feeReceiver, fee);
        }

        _updateSpentAmounts(msg.sender, _amount);
        users[msg.sender].totalTransactions++;

        emit WithdrawalMade(msg.sender, _token, amountAfterFee);
    }

    /**
     * @dev Transfer tokens between users
     */
    function transfer(address _to, address _token, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyVerifiedUser 
        supportedToken(_token) 
    {
        require(_to != address(0), "Invalid recipient");
        require(_amount > 0, "Amount must be greater than 0");
        require(users[_to].isRegistered, "Recipient not registered");
        require(balances[msg.sender][_token].available >= _amount, "Insufficient balance");
        require(_checkLimits(msg.sender, _amount), "Transaction limit exceeded");

        // Calculate fee
        uint256 fee = _amount.mul(platformFee).div(10000);
        uint256 amountAfterFee = _amount.sub(fee);

        // Update balances
        balances[msg.sender][_token].available = balances[msg.sender][_token].available.sub(_amount);
        balances[_to][_token].available = balances[_to][_token].available.add(amountAfterFee);

        // Transfer fee to platform
        if (fee > 0) {
            balances[feeReceiver][_token].available = balances[feeReceiver][_token].available.add(fee);
        }

        _updateSpentAmounts(msg.sender, _amount);
        users[msg.sender].totalTransactions++;
        users[_to].totalTransactions++;

        // Update credit scores
        _updateCreditScore(msg.sender, true);
        _updateCreditScore(_to, true);

        emit TransferMade(msg.sender, _to, _token, amountAfterFee);
    }

    /**
     * @dev Cash-in operation (agent to user)
     */
    function cashIn(address _user, address _token, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyActiveAgent 
        supportedToken(_token) 
    {
        require(_user != address(0), "Invalid user");
        require(_amount > 0, "Amount must be greater than 0");
        require(users[_user].isRegistered, "User not registered");
        require(balances[msg.sender][_token].available >= _amount, "Insufficient agent balance");

        // Calculate agent commission
        uint256 commission = _amount.mul(agents[msg.sender].commissionRate).div(10000);
        uint256 userAmount = _amount.sub(commission);

        // Update balances
        balances[msg.sender][_token].available = balances[msg.sender][_token].available.sub(_amount);
        balances[_user][_token].available = balances[_user][_token].available.add(userAmount);
        if (commission > 0) {
            balances[msg.sender][_token].available = balances[msg.sender][_token].available.add(commission);
        }

        // Update agent stats
        agents[msg.sender].totalVolume = agents[msg.sender].totalVolume.add(_amount);
        agents[msg.sender].successfulTransactions++;

        emit CashInOutCompleted(_user, msg.sender, userAmount, true);
    }

    /**
     * @dev Cash-out operation (user to agent)
     */
    function cashOut(address _agent, address _token, uint256 _amount) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyVerifiedUser 
        supportedToken(_token) 
    {
        require(_agent != address(0), "Invalid agent");
        require(_amount > 0, "Amount must be greater than 0");
        require(agents[_agent].isActive, "Agent not active");
        require(balances[msg.sender][_token].available >= _amount, "Insufficient balance");
        require(_checkLimits(msg.sender, _amount), "Transaction limit exceeded");

        // Calculate agent commission
        uint256 commission = _amount.mul(agents[_agent].commissionRate).div(10000);
        uint256 agentAmount = _amount.sub(commission);

        // Update balances
        balances[msg.sender][_token].available = balances[msg.sender][_token].available.sub(_amount);
        balances[_agent][_token].available = balances[_agent][_token].available.add(agentAmount);
        if (commission > 0) {
            balances[_agent][_token].available = balances[_agent][_token].available.add(commission);
        }

        _updateSpentAmounts(msg.sender, _amount);
        users[msg.sender].totalTransactions++;

        // Update agent stats
        agents[_agent].totalVolume = agents[_agent].totalVolume.add(_amount);
        agents[_agent].successfulTransactions++;

        emit CashInOutCompleted(msg.sender, _agent, agentAmount, false);
    }

    /**
     * @dev Get user balance for a specific token
     */
    function getBalance(address _user, address _token) external view returns (uint256 available, uint256 locked) {
        return (balances[_user][_token].available, balances[_user][_token].locked);
    }

    /**
     * @dev Get user by phone number
     */
    function getUserByPhone(string memory _phoneNumber) external view returns (address) {
        return phoneToAddress[_phoneNumber];
    }

    /**
     * @dev Get supported tokens list
     */
    function getSupportedTokens() external view returns (address[] memory) {
        return tokenList;
    }

    /**
     * @dev Check transaction limits
     */
    function _checkLimits(address _user, uint256 _amount) internal view returns (bool) {
        uint256 currentDay = block.timestamp / 86400; // seconds in a day
        uint256 currentMonth = block.timestamp / (86400 * 30); // approximate month

        return (
            dailySpent[_user][currentDay].add(_amount) <= dailyLimit &&
            monthlySpent[_user][currentMonth].add(_amount) <= monthlyLimit
        );
    }

    /**
     * @dev Update spent amounts for limits
     */
    function _updateSpentAmounts(address _user, uint256 _amount) internal {
        uint256 currentDay = block.timestamp / 86400;
        uint256 currentMonth = block.timestamp / (86400 * 30);

        dailySpent[_user][currentDay] = dailySpent[_user][currentDay].add(_amount);
        monthlySpent[_user][currentMonth] = monthlySpent[_user][currentMonth].add(_amount);
    }

    /**
     * @dev Update user credit score
     */
    function _updateCreditScore(address _user, bool isPositive) internal {
        if (isPositive && users[_user].creditScore < 850) {
            users[_user].creditScore = users[_user].creditScore.add(1);
        } else if (!isPositive && users[_user].creditScore > 300) {
            users[_user].creditScore = users[_user].creditScore.sub(5);
        }
    }

    /**
     * @dev Update transaction limits (only owner)
     */
    function updateLimits(uint256 _dailyLimit, uint256 _monthlyLimit) external onlyOwner {
        dailyLimit = _dailyLimit;
        monthlyLimit = _monthlyLimit;
    }

    /**
     * @dev Update platform fee (only owner)
     */
    function updatePlatformFee(uint256 _platformFee) external onlyOwner {
        require(_platformFee <= 1000, "Platform fee too high"); // Max 10%
        platformFee = _platformFee;
    }

    /**
     * @dev Update fee receiver (only owner)
     */
    function updateFeeReceiver(address _feeReceiver) external onlyOwner {
        require(_feeReceiver != address(0), "Invalid fee receiver");
        feeReceiver = _feeReceiver;
    }

    /**
     * @dev Pause the contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdrawal (only owner, when paused)
     */
    function emergencyWithdraw(address _token, uint256 _amount) external onlyOwner whenPaused {
        IERC20(_token).safeTransfer(owner(), _amount);
    }
}

